import React, { useRef } from 'react';
import './styles/app.css';
import Header from './components/Header.js';
import Home from './components/Home.js';
import AboutMe from './components/AboutMe.js';
import Portfolio from './components/Portfolio.js';

function App() {
  const scrollToRef = ref => {
    ref.current.scrollIntoView( {behavior: 'smooth', block: 'start'});
  };
  const home = useRef(null);
  const aboutMe = useRef(null);
  const portfolio = useRef(null);

  return (

    <div className="App">
      <Header scrollTRef = {scrollToRef} home = {home} aboutMe = {aboutMe} portfolio = {portfolio} />

      <div ref = {home} style = {{ height: '100vh', alignItems: 'center', justifyContent: 'center'}}> 
      <Home/>
      </div>
      <hr className="section-divider" />

      <div ref = {aboutMe} style = {{height: '100vh', alignItems: 'center', justifyContent: 'center'}}> 
      <AboutMe/>
      </div>
      <hr className="section-divider" />

      <div ref = {portfolio} style = {{height: '100vh,', alignItems: 'center', justifyContent: 'center'}}>
      <Portfolio/>
      </div>


    </div>
  );
}

export default App;
